type Extraccion = (Int,String,String,Int)
type Extracciones = [Extraccion]

-- Encabezado: "extracciones_productos_forestales_anio","extraccion_productos_forestales_tipo","extraccion_productos_forestales_provincia","extraccion_productos_forestales_numero","extraccion_productos_forestales_unidad_medida"



--Proyyeciones.


anio :: Extraccion -> Int
anio (an,prod,prov,cant) = an

producto :: Extraccion -> String
producto (an,prod,prov,cant) = prod

provincia :: Extraccion -> String
provincia (an,prod,prov,cant) = prov

cantidad :: Extraccion -> Int
cantidad (an,prod,prov,cant) = cant 

--Funciones para analizar la base.

total_Extraido :: Extracciones -> Int
total_Extraido [] = 0
total_Extraido (x:xs) = cantidad x + total_Extraido xs
--Ésta función toma la base completa y suma las toneladas extraidas de todas las provincias durante todos los años registrados.
--La función es de tipo fold.



extraccionesAnios ::  Int -> Int ->Extracciones -> Extracciones
extraccionesAnios a b [] = []
extraccionesAnios a b (x:xs)|((anio x) >= a) && ((anio x) <= b) = x : (extraccionesAnios a b xs)
                            |otherwise = extraccionesAnios a b xs
--Ésta es una funcion de tipo filter. Lo que hace es que dado un año "base" y otro "tope" devuelve las extracciones comprendidas entre esos años.

elijeProvincia :: String -> Extracciones -> Extracciones
elijeProvincia p [] = []
elijeProvincia p (x:xs)|(provincia x) == p = x : (elijeProvincia p xs)
                       |otherwise = elijeProvincia p xs
--Ésta función también es de tipo filter y lo que hace es mostrar todas las extracciones de la provincia que elijamos.

elijeAnio :: Int -> Extracciones -> Extracciones
elijeAnio a [] = []
elijeAnio a (x:xs)|(anio x) == a = x : (elijeAnio a xs)
                  |otherwise = elijeAnio a xs
--De la misma manera que la función anterior, ésta es una filter que muestra las extracciones realizadas en el año que elijamos.

elijeProducto :: String -> Extracciones -> Extracciones
elijeProducto t [] = []
elijeProducto t (x:xs)|(producto x) == t = x : (elijeProducto t xs)
                      |otherwise = elijeProducto t xs 
--Ésta función hace lo mismo que las anteriores pero filtra según el producto.




--Habiendo definido las funciones anteriores, voy a combinar algunas para hacer un par de funciones mas que sirvan para analizar la base.

busqueda :: Extracciones -> Int -> String -> String ->  Int
busqueda [] an prod prov  = 0
busqueda (x:xs) an  prod prov |((anio x) == an) && ((provincia x) == prov) && (producto x) == prod = cantidad x
                              |otherwise = busqueda xs an  prod prov 
--Ésta funcion devuelve las toneladas de la extraccion según el año, producto y provincia que deseemos encontrar.



totalProvincia :: String -> Extracciones -> Int
totalProvincia prov xs = total_Extraido(elijeProvincia prov xs)   
--Calcula el total de toneladas extraidas de una provincia.   

sacaAnio :: Extracciones -> [(String,String,Int)]
sacaAnio [] = []
sacaAnio ((a,b,c,d):xs) = (b,c,d) : (sacaAnio xs)
